'use strict';
var Buffer = require('buffer').Buffer; // browserify
var SlowBuffer = (require('buffer').SlowBuffer) || undefined;

module.exports = bufferEq;

function bufferEq(a, b) {
  if (!Buffer.isBuffer(a) || !Buffer.isBuffer(b)) {
    return false;
  }
  if (a.length !== b.length) {
    return false;
  }
  var c = 0;
  for (var i = 0; i < a.length; i++) {
    /*jshint bitwise:false */
    c |= a[i] ^ b[i];
  }
  return c === 0;
}

bufferEq.install = function() {
  var eq = function equal(that) {
    return bufferEq(this, that);
  };
  Buffer.prototype.equal = eq;
  if (typeof SlowBuffer !== 'undefined' && SlowBuffer) {
    SlowBuffer.prototype.equal = eq;
  }
};

var origBufEqual = Buffer.prototype.equal;
var origSlowBufEqual = (typeof SlowBuffer !== 'undefined' && SlowBuffer) ? SlowBuffer.prototype.equal : undefined;
bufferEq.restore = function() {
  Buffer.prototype.equal = origBufEqual;
  if (typeof SlowBuffer !== 'undefined' && typeof origSlowBufEqual !== 'undefined') {
    SlowBuffer.prototype.equal = origSlowBufEqual;
  }
};
